import React, { Component } from 'react';
import ProfQues from '../Pages/Profile/ProfQues'

class Search extends Component {

    constructor(props) {
        super(props);

        this.state = {
            data: []
        }
    }

    componentDidMount() {
        var requestOptions = {
            method: 'GET',
            redirect: 'follow'
        };

        fetch("https://recmonk.herokuapp.com/posts", requestOptions)
            .then(response => response.json())
            .then(result => {
                console.log(result)
                let FilteredTitle =
                    result.filter((item) => {
                        if (item.isQuestion == "true")
                            return item.name.toLowerCase().includes(this.props.title.toLowerCase())
                    })
                this.setState({ data: FilteredTitle })
            })
            .catch(error => console.log('error', error));
    }

    render() {
        return (
            <div>

            </div >
        )
    }
}

export default Search
