import React, { Component } from 'react';
import { Grid, Link, Button } from '@material-ui/core';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
import './community.css';
import '../Page.css';

function Monks(props) {
    return (
        <div>
            {
                props.props.map((data) => {
                    if (data.points >= 101) {
                        return (
                            <Grid container style={{ marginBottom: '20px' }} >
                                <Grid item>
                                    <Avatar style={{ width: '60px', height: '60px' }} src="" />
                                </Grid>
                                <Grid item style={{ marginTop: '-5px', marginLeft: '20px' }}>
                                    <Typography variant="h6">{data.name}<span><img alt="Verified" title="Verified" src="assets/images/verified.png" /></span></Typography>
                                    <Typography variant="caption">Engagement Score: {data.points}</Typography>
                                </Grid>
                            </Grid>
                        );
                    }
                })
            }
        </div>
    );
}

function Users(props) {

    const [row, setRow] = React.useState(10);

    const showMore = () => {
        let dataLength = row;
        setRow({ row: dataLength += 10 });
    }
    return (
        <div align="center">
            {
                props.props.slice(0, row).map((data) => {
                    if (data.points > 0 && data.points <= 15) {
                        return (
                            <Grid container style={{ marginBottom: '20px' }} >
                                <Grid item>
                                    <Avatar style={{ width: '60px', height: '60px' }} src="" />
                                </Grid>
                                <Grid item style={{ marginTop: '-5px', marginLeft: '20px' }}>
                                    <Typography variant="h6">{data.name}</Typography>
                                    <Typography variant="caption">Engagement Score: {data.points}</Typography>
                                </Grid>
                            </Grid>
                        );
                    }
                })
            }
            <Button variant="contained" size="small" color="primary">Load more</Button>
        </div>
    );
}

function Enlightners(props) {
    return (
        <div>
            {
                props.props.map((data) => {
                    if (data.points >= 16 && data.points <= 100) {
                        return (
                            <Grid container style={{ marginBottom: '20px' }} >
                                <Grid item>
                                    <Avatar style={{ width: '60px', height: '60px' }} src="" />
                                </Grid>
                                <Grid item style={{ marginTop: '-5px', marginLeft: '20px' }}>
                                    <Typography variant="h6">{data.name}</Typography>
                                    <Typography variant="caption">Engagement Score: {data.points}</Typography>
                                </Grid>
                            </Grid>
                        );
                    }
                })
            }
        </div>
    );
}

function TabPanel(props) {
    const { children, value, index } = props;

    return (
        <div
            role="tabpanel"
            hidden={value !== index}
            id={`community-tabpanel-${index}`}
            aria-labelledby={`community-tab-${index}`}
            className="community-tabpanel"
        >
            {value === index && (
                <div>{children}</div>
            )}
        </div>
    );
}

class Community extends Component {

    constructor(props) {
        super(props);

        this.state = {
            value: 0,
            data: []
        }

        this.handleChange = this.handleChange.bind(this);
    }

    componentDidMount() {
        this.getData();
    }

    getData = () => {
        var requestOptions = {
            method: 'GET',
            redirect: 'follow'
        };

        fetch("https://recmonk.herokuapp.com/members", requestOptions)
            .then(response => response.json())
            .then(result => {
                this.setState({ data: result })
            })
            .catch(error => console.log('error', error));
    }

    handleChange = (event, index) => {
        this.setState({
            value: index
        });
    };

    render() {
        return (
            <div className="Community" style={{ paddingBottom: '50px' }}>
                <Tabs value={this.state.value} onChange={this.handleChange} aria-label="community-tabs" className="community-tabs" >
                    <Tab label="Members" />
                    <Tab label="Enlighteners" />
                    <Tab label="Monks" />
                </Tabs>
                <Typography className="community-tabs-info" variant="caption">Ranked based on insightful engagement. <Link>Details</Link></Typography>

                <TabPanel value={this.state.value} index={0}>
                    <Users props={this.state.data} />
                </TabPanel>
                <TabPanel value={this.state.value} index={1}>
                    <Enlightners props={this.state.data} />
                </TabPanel>
                <TabPanel value={this.state.value} index={2}>
                    <Monks props={this.state.data} />
                </TabPanel>
            </div>
        );
    }
}

export default Community;
