import React, { useState } from 'react'
import { Button } from '@material-ui/core'
import {Link, useHistory} from 'react-router-dom'
import '../universal.css'
import Filter from './Filter'

function BottomHeader() {

    const history = useHistory();

    const [form, setForm] = useState({
        value: ''
    });

    const handleChange = e => {
        setForm({
            ...form,
            [e.target.name]: e.target.value
          });
    }

    const finalStep = e=>{
        e.preventDefault();

        if(form.value == null)
        {
            alert('Please write to search')
        }
        else
        {
            history.push(`/add-post/${form.value}`)
        }
    }

    return (
        <div>
        <div className="BottomHeader">
            <form className="myForm" >
                <input type="search" name='search' value={form.value} onChange={handleChange} required placeholder="Question monk's wisdom, you seek!" />
                <button onClick={finalStep} type="submit">
                    Ask
                </button>
            </form>
        </div>
        </div>
    )
}

export default BottomHeader
