        })
    }

    var requestOptions = {
        method: 'GET',
        redirect: 'follow'
    };

    let arr

      fetch("https://recmonk.herokuapp.com/posts", requestOptions)
        .then(response => response.json())
        .then(result => {
            result.map(item => {
                if(item.isQuestion == "true")
                    arr.push(item)
            })

            setState({ data: arr })
        })
