import {SET_AUTH_TOKEN} from '../constants/index';

export default function authReducer(state, action){
    switch(action.type){
        case SET_AUTH_TOKEN:
            return{
                ...state,
                isAuthenticated:true,
                authUser : action.payload
            }

    default:
        return state;
}
}
