import {SET_AUTH_TOKEN} from '../constants/index';

export default function authReducer(state={
    authUser : null,
    isAuthenticated:false
}, action){
    switch(action.type){
        case SET_AUTH_TOKEN:
            return{
                isAuthenticated:true,
                authUser : action.payload.auth
            }

    default:
        return state;
}
}
