import React,{Component} from 'react'
import TuneIcon from '@material-ui/icons/Tune';
import '../universal.css'
import { fetchProfile } from '../../../redux/dataFetchers/ProfileApi';
import { withRouter } from 'react-router-dom';
import { connect } from 'react-redux';

const mapDispatchToProps = (dispatch) => ({
    fetchProfile: () => { dispatch(fetchProfile()) }
});

class Filter extends Component {

    constructor(props) {
        super(props);

        this.click = 'false'
    }

    componentDidMount() {
        this.props.fetchProfile();
    }

    showList = () =>{
        this.setClick('true');
    }
    hideList =()=>{
        this.setClick('false')
    }

    displayItem = (e)=>{
        return(
            <div className="btmFilter">
                <p>Category</p>
                <p>Last Week</p>
            </div>
        )
    }

    render() {
    return (
        <div className="Filter" container>
            <div className="topFilter">
                 <p>My Feed</p>
                 <TuneIcon size="small" onClick={this.click == 'false' ? this.showList : this.hideList} style={{color:'#000', cursor: 'pointer'}}/>
            </div>
            {this.click == 'true' ? this.displayItem() : this.hideList}
        </div>
    )
}
}

export default withRouter(connect(mapDispatchToProps)(Filter));
