import React, { Component } from 'react';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import DescriptionIcon from '@material-ui/icons/Description';
import MenuBookIcon from '@material-ui/icons/MenuBook';
import MovieIcon from '@material-ui/icons/Movie';
import TuneIcon from '@material-ui/icons/Tune';
import Articles from './Articles';
import { InputLabel, NativeSelect, ListItemIcon, ListItemText } from '@material-ui/core';
import BookPdf from '../../../QnA/Question/BookPdf';
import Videos from './Videos';
import '../SideBar.css'
import './Library.css'
import { fetchLibrary } from '../../../../redux/dataFetchers/Libapi'
import { withRouter } from 'react-router-dom';
import { connect } from 'react-redux';


const mapStateToProps = state => {
  return {
    library: state.lib.lib
  }
}

const mapDispatchToProps = (dispatch) => ({
  fetchLibrary: () => { dispatch(fetchLibrary()) }
});


class Library extends Component {
  constructor(props) {
    super(props);

    this.state = {
      content: 'Articles',
      click: false
    }
  }

  handleChange = (event) => {
    this.setState({
      content: event.target.value
    })
  };

  showList = () => {
    this.setState({
      click: true
    })
  }
  hideList = () => {
    this.setState({
      click: false
    })
  }

  displayItem = () => {
    return (
      <div className="btmFilter">
        <p>Category</p>
        <p>Last Week</p>
      </div>
    )
  }

  componentDidMount() {
    this.props.fetchLibrary();
  }

  render() {
    console.log(this.props);

    return (
      <div className="LibraryHeader">
        <div className="libraryHead">
          <div className="libraryHead1">
            <InputLabel htmlFor="library">Library</InputLabel>
            <Select id="library" label="Library" name="content"
              value={this.state.content} onChange={this.handleChange}>
              <MenuItem value="Articles">
                <ListItemIcon>
                  <DescriptionIcon />
                </ListItemIcon>
                <ListItemText primary="Articles" />
              </MenuItem>
              <option value="Articles">Articles</option>
              <option value="BookPdfs"><i class="fa fa-file-pdf-o"></i> BookPdfs</option>
              <option value="Videos"><MovieIcon fontSize='small' style={{ color: '#707070', marginRight: '10px' }} /> Videos</option>
            </Select>
          </div>
          <div className="Filter" container>
            <div className="topFilter">
              <TuneIcon size="small" onClick={this.state.click == 'false' ? this.showList : this.hideList} style={{ color: '#000', cursor: 'pointer' }} />
            </div>
          </div>
        </div>
        {this.state.click == 'true' ? this.displayItem : this.hideList}
        <div style={{ height: '20px' }}></div>
        <div>
          {
            (this.state.content == 'Articles') ? <Articles /> : (this.state.content == 'BookPdfs') ? <BookPdf /> : <Videos />
          }
        </div>
      </div>
    );
  }
}

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(Library));
