import React from 'react'
import StarsIcon from '@material-ui/icons/Stars';
import FilterListIcon from '@material-ui/icons/FilterList';
import "./QnA.css"
function PopularPosts() {

     // Filter style
     const StyledMenu = withStyles({
        paper: {
          border: '1px solid #d3d4d5',

        },
      })((props) => (
        <Menu
          elevation={0}
          getContentAnchorEl={null}
          anchorOrigin={{
            vertical: 'bottom',
            horizontal: 'center',
          }}
          transformOrigin={{
            vertical: 'top',
            horizontal: 'center',
          }}
          {...props}
        />
      ));

    const StyledMenuItem = withStyles((theme) => ({
        root: {
          '&:focus': {
            backgroundColor: "#B02911",
            '& .MuiListItemIcon-root, & .MuiListItemText-primary': {
              color: theme.palette.common.white,
            },
          },
        },
      }))(MenuItem);

      const [anchorEl, setAnchorEl] = React.useState(null);

        const handleClick = (event) => {
          setAnchorEl(event.currentTarget);
        };

        const handleClose = () => {
          setAnchorEl(null);
        };

    //logic: Here, we will use state to fetch the popular posts to display.
    return (
        <div className="PopularPosts">
            <div className="postTitle">
                <div className="postTitle1">
                    <StarsIcon fontSize="medium" style={{color: '#545454'}} />
                    <h3>Popular Posts</h3>
                </div>
                <div>
                    <FilterListIcon  aria-controls="customized-menu"
                                aria-haspopup="true"
                                onClick={handleClick}
                                style={{color: '#707070', margin: '0px 10px'}}/>
                     <StyledMenu
                                id="customized-menu"
                                anchorEl={anchorEl}
                                keepMounted
                                open={Boolean(anchorEl)}
                                onClose={handleClose}>
                                <StyledMenuItem>
                                <ListItemIcon>
                                <BookmarkBorderOutlinedIcon style={{color: '#707070', margin: '0px 10px'}}/>
                                </ListItemIcon>
                                <ListItemText primary="Save post" />
                                </StyledMenuItem>
                                <StyledMenuItem>
                                <ListItemIcon>
                                <ReportOutlinedIcon style={{color: '#707070', margin: '0px 10px'}}/>
                                </ListItemIcon>
                                <ListItemText primary="Report post" />
                                </StyledMenuItem>
                     </StyledMenu>
                </div>

            </div>
            <div className="allPosts">
                <div className="post">
                    <p>245 k</p>
                    <p>What are the best tools for hiring</p>
                </div>
                <div className="post">
                    <p>92</p>
                    <p>What are the best tools for hiring</p>
                </div>
                <div className="post">
                    <p>89</p>
                    <p>What are the best tools for hiring</p>
                </div>
                <div className="post">
                    <p>87</p>
                    <p>What are the best tools for hiring</p>
                </div>
                <div className="post">
                    <p>82</p>
                    <p>What are the best tools for hiring</p>
                </div>
                <div className="post">
                    <p>78</p>
                    <p>What are the best tools for hiring</p>
                </div>
            </div>
        </div>
    )
}


export default PopularPosts
