import React from 'react';
import clsx from 'clsx';
import { makeStyles } from '@material-ui/core/styles';
import SwipeableDrawer from '@material-ui/core/SwipeableDrawer';
import Button from '@material-ui/core/Button';
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import MailIcon from '@material-ui/icons/Mail';
import MenuIcon from '@material-ui/icons/Menu';
import logo from '../../../img/logo.png'
import './universal.css'
import LiveHelpIcon from '@material-ui/icons/LiveHelp';
import MovieIcon from '@material-ui/icons/Movie';

const useStyles = makeStyles({
  list: {
    width: 250,
  },
  fullList: {
    width: 'auto',
  },
});

export default function TopHeader() {
  const classes = useStyles();
  const [state, setState] = React.useState({
    left: false,
  });

  const toggleDrawer = (anchor, open) => (event) => {
    if (event && event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
      return;
    }

    setState({ ...state, [anchor]: open });
  };

  const list = (anchor) => (
    <div
      className={clsx(classes.list, {
        [classes.fullList]: anchor === 'top' || anchor === 'bottom',
      })}
      role="presentation"
      onClick={toggleDrawer(anchor, false)}
      onKeyDown={toggleDrawer(anchor, false)}
    >
      <List>
        <ListItem button>
                <ListItemIcon> <LiveHelpIcon style={{color : '#b32800'}}/> </ListItemIcon>
                <ListItemText primary='Question'  style={{color : '#b32800'}}/>
        </ListItem>
        <ListItem button>
                <ListItemIcon> <MailIcon style={{color : '#b32800'}}/> </ListItemIcon>
                <ListItemText primary='FolloMont - Bot'  style={{color : '#b32800'}}/>
        </ListItem>
        <ListItem button>
                <ListItemIcon> <MovieIcon style={{color : '#b32800'}} /> </ListItemIcon>
                <ListItemText primary='Videos'  style={{color : '#b32800'}}/>
        </ListItem>

      </List>
      <Divider />
    </div>
  );

  const sideBar = ()=>{
      return(
        ['left'].map((anchor) => (
            <React.Fragment key={anchor}>
              <Button onClick={toggleDrawer(anchor, true)}>
                  <MenuIcon style={{ color: '#b32800' }} fontSize="medium" />
              </Button>
              <SwipeableDrawer
                anchor={anchor}
                open={state[anchor]}
                onClose={toggleDrawer(anchor, false)}
                onOpen={toggleDrawer(anchor, true)}
              >
                {list(anchor)}
              </SwipeableDrawer>
            </React.Fragment>
          ))
      )
  }

  const logoImg = () =>{
      return(
          <div className="logo">
              <img src={logo}></img>
          </div>
      )
  }

  const signin = () =>{
    return(
        <div className="signin">
            <p>Sign in</p>
        </div>
    )
  }

  return (
    <div className="TopHeader">
        {sideBar()}

        {logoImg()}

        {signin()}

    </div>
  );
}
