import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import { Button, Divider, FormGroup, withStyles } from '@material-ui/core';
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid';
import AccountCircle from '@material-ui/icons/AccountCircle';
import LockIcon from '@material-ui/icons/Lock';
import Checkbox from '@material-ui/core/Checkbox';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import AccountCircleIcon from '@material-ui/icons/AccountCircle';
import {Link, useHistory} from 'react-router-dom'
import { store } from '../../../redux/reducers/index'
import LinkedInIcon from '@material-ui/icons/LinkedIn';
import Signup from './Signup';
import Forget from './Forget';
import '../Auth.css'
import Swal from 'sweetalert2'


const useStyles = makeStyles((theme) => ({
  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  paper: {
    backgroundColor: theme.palette.background.paper,
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
    borderRadius: '7px'
  },
}));


export default function Sign() {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);
  const history = useHistory();
  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };


  // For hanlding the remember user.

  const [checked, setChecked] = React.useState(true);

  const handleCheck = (e) => {
    setChecked(e.target.checked)
  }

  const [state, setState] = React.useState({
    username: '',
    password: ''
  });

  const handleChange = (event) => {
    setState({
      ...state,
      [event.target.name]: event.target.value
    });
  };

  const finalstep = (e) => {

    var raw = JSON.stringify(state);

    var myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");

    var requestOptions = {
      method: 'POST',
      headers: myHeaders,
      body: raw,
      redirect: 'follow'
    };

    return fetch("https://recmonk.herokuapp.com/login", requestOptions)

      .then(response => {
        if (response.ok) {
          handleClose();
          return response;
        }
        else {
          handleClose();
          var error = new Error("Error " + response.status + ": " + response.statusText);
          error.response = response;
          throw error;
        }
      },
        error => {
          var errmess = new Error(error.message);
          throw errmess;
        })
      .then(response => response.json())

      .then(result => {
        console.log(result)

          //to update the state 
          store.subscribe(()=>{
            console.warn('my redux: ', store.getState())
            console.log('user: ', store.getState().authUser.authUser)
        })

            //Action for state
        const addUser = text=>{
            return{
                type: "SET_AUTH_TOKEN",
                payload: {auth: text}
            }
        }

        localStorage.setItem("token", result.token);

            //assigning the new value to the state
        store.dispatch(addUser(result.token));

      })
      .catch(error => {
        console.log('Error', error.message)
        Swal.fire({
          icon: 'error',
          title: 'Oops...',
          text: error.message
        })
      });
  }

  // const doSignUp =()=>{
  //   document.getElementById('siginModalId').style.display = 'none';
  // }

  return (
    <div className="Sign" id="idSign">
      {
        localStorage.getItem('token') ? 
        <AccountCircleIcon onClick={()=>history.push('/profile')} fontSize='large'  style={{color: '#b32800', position: 'absolute', top: '0', right: '16px', cursor: 'pointer'}}/> :
        <Button className="signBtn" style={{ color: '#B0343C', fontWeight: 'bold', position: 'absolute', top: '8px', right: '16px' }} type="button" onClick={handleOpen}>
        Sign in
        </Button>
      }


      <Modal
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        className={classes.modal}
        open={open}
        onClose={handleClose}
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
          timeout: 500,
        }}
      >
      <Grid container className="modal-container">
        <Grid item style={{ backgroundColor: '#fff' }}>
          <img src="/assets/images/logo-recruitingmonk-vertical-color-transbg.png" />
        </Grid>
        <Grid item>
        <Fade in={open} className="signinModal" id="siginModalId">
          <div className={classes.paper}>
            <div className="apiBtn" id="transition-modal-title">
              <Button id="transition-modal-description" variant="contained" style={{ backgroundColor: '#db4e40', color: '#fff', fontSize: '18px' }}> <span style={{ fontSize: '22px', marginRight: '20px' }} class="fa fa-google"> </span> Continue with <p style={{ fontWeight: 'bold' }}>&nbsp;Google</p></Button>
              <Button id="transition-modal-description" variant="contained" style={{ backgroundColor: '#0174b4', color: '#fff', fontSize: '18px' }}> <span style={{ fontSize: '22px', marginRight: '20px' }} class="fa fa-linkedin"> </span> Continue with <p style={{ fontWeight: 'bold' }}>&nbsp;LinkedIn</p></Button>
            </div>

            <div class="separator">or use</div>

            <div className="inputFields">

              <div className={classes.margin} >
                <div>
                  <Grid container spacing={1} alignItems="flex-end">
                    <Grid item>
                      <AccountCircle style={{ color: '#767676' }} />
                    </Grid>
                    <Grid item>
                      <TextField id="input-with-icon-grid" label="Email" name="username"
                        value={state.username}
                        onChange={handleChange}
                        fullWidth
                        type="email" required />
                    </Grid>
                  </Grid>
                </div>

                <div>
                  <Grid container spacing={1} alignItems="flex-end">
                    <Grid item>
                      <LockIcon style={{ color: '#767676' }} />
                    </Grid>
                    <Grid item>
                      <TextField id="input-with-icon-grid" label="Password"
                        name="password"
                        value={state.password}
                        onChange={handleChange}
                        fullWidth
                        type="password" required />
                    </Grid>
                  </Grid>
                </div>
              </div>
            </div>

            <div className="addtnlLogTool">
              <FormGroup
                style={{ fontSize: '10px' }}
              >
                <FormControlLabel className="addtnlLogTool"
                  control={<Checkbox checked={checked} onChange={handleCheck} name="checkedMe" style={{ color: '#767676', fontSize: '10px' }} size="small" />}
                  label="Remember Me!"
                />
              </FormGroup>

              <p style={{ color: '#B0343C', cursor: 'pointer' }}>
                <Forget />
              </p>
            </div>

            <div className="logBtn">
              <Button variant="contained" container style={{ backgroundColor: '#B0343C', color: '#fff', border: 'none', width: '80vw' }}
                onClick={finalstep} >
                Login
              </Button>
            </div>
            <div className="signUpBtn">
              <p>Don't have account, </p>
              {/* <Button color="primary">Sign Up Here</Button> */}
              <p><Signup /></p>
            </div>
          </div>
        </Fade>
        </Grid>
        </Grid>
      </Modal>
    </div>
  );
}
