import React,{useState} from 'react'
import TuneIcon from '@material-ui/icons/Tune';

function Filter() {

    const [click, setClick] = useState('false');

    const showList = () =>{
        if(click === 'true')
        {
            return(
            <div className="btmFilter" id="btmFilterId">
               <p>Category</p>
               <p>Last Week</p>
            </div>
        );
        }
    }

    return (
        <div className="Filter" container>
            <div className="topFilter">
                 <p>My Feed</p>
                 <TuneIcon size="small" onClick={()=>setClick('true')} style={{color:'#000'}}/>
            </div>
            {showList}
        </div>
    )
}

export default Filter
